import React from 'react'
import {problems_list, user} from "./globals";
import Header from "../model";
import {connect} from "react-redux";
import {toLogin} from "../action";
import {Link} from "react-router-dom";

class register extends React.Component {
    constructor(props) {
        super(props);
        this.login = '';
        this.password = '';

        this.state = {login: '', password: ''};

        this.handleLoginChange = this.handleLoginChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    // state = {
    //     login: '',
    //     password: '',
    // }

    handleLoginChange(event) {
        this.setState({login: event.target.value});
    }

    handlePasswordChange(event) {
        this.setState({password: event.target.value});
    }

    handleSubmit(event) {
        event.preventDefault();
        const {
            login,
            password
        } = this.state;


        this.props.register(login, password).then(() => {
            this.props.history.push(`/profile/${this.props.user.login}`);
        })
    }

    handleKeyDown = event => {
        if (event.key === 'Enter') {
            event.preventDefault();
        }
    }


    render() {
        let {login, password} = this.state;
        return (
            <div>

                <form>
                    <label>
                        Логин:
                        <input type="text" value={login} onChange={this.handleLoginChange}
                               onKeyDown={this.handleKeyDown}/>
                        Пароль:
                        <input type="password" value={password} onChange={this.handlePasswordChange}
                               onKeyDown={this.handleKeyDown}/>
                    </label>
                    <button type="submit" value="Войти" className="btn btn-primary"
                            onClick={this.handleSubmit}>Зарегистрироваться
                    </button>
                </form>
                <Link to='/login' className="btn btn-primary">Войти</Link>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        user: state.userReducer.user
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        register: (...args) => dispatch(toRegister(...args)),
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(register);
