import React, {useState} from 'react'
import UserService from '../user'

export default function LoginPage(props) {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [errorText, setErrorText] = useState('');


    function onChangeLogin(event) {
        setErrorText('');
        setLogin(event.target.value)
    }

    function onChangePassword(event) {
        setErrorText('');
        setPassword(event.target.value)
    }

    function onLogin(event) {
        event.preventDefault();

        UserService.login(login, password)
            .then((user) => {
                props.setUser(user);

                props.history.push('/');
            })
            .catch((error) => {
                setErrorText(error.error)
            })
    }

    function onToSignUp(event) {
        event.preventDefault();

        props.history.push('/signup');
    }

    return (
        <form>
            <h2>Добро пожаловать</h2>
            <div>
                <input name="email" type="email" placeholder="Логин" onChange={onChangeLogin} value={login}/>
                <input name="password" type="password" placeholder="Пароль" onChange={onChangePassword} value={password}/>
            </div>
            <div>
                <button onClick={onLogin} className="btn btn-primary">Войти</button>
                <button onClick={onToSignUp} className="btn btn-primary">Нет аккаунта</button>
            </div>
            <p>{errorText}</p>
        </form>
    )
}
