import User from '../User/user'

let initialState ={
    user: new User('', '', false)
}

const userReducer = (state = initialState, action) => {
    switch(action.type) {
        case 'USER_ISLOGGED':
            return {
                ...state,
                user: action.user.user
            };
        default:
            return state;
    }
}

export default userReducer;
