import React from 'react';
import {connect} from "react-redux";
import Problem from "../problem/Problem";

class EditForm extends React.Component {
    // constructor({match, props}) {
    //     super(props);
    //     const {params: {problem_id}} = match;
    //     this.id = problem_id;
    //     console.log(')')
    //     // console.log(this)
    //     // console.log(this.id);
    //
    //
    //
    //     // this.handleChange = this.handleChange.bind(this);
    //     // this.handleSubmit = this.handleSubmit.bind(this);
    // }
    problem = this.props.problems_list.get(Number(this.id))
    state = {name: this.problem.name,
        task: this.problem.task,
        solution: this.problem.solution,
        author: this.problem.author}

    handleChangeName(event) {
        this.setState({name: event.target.value});
    }
    handleChangeTask(event) {
        this.setState({task: event.target.value});
    }
    handleChangeSolution(event) {
        this.setState({solution: event.target.value});
    }
    handleChangeAuthor(event) {
        this.setState({author: event.target.value});
    }


    handleSubmit(event) {

        this.props.history.push(`/problem/${this.id}`)
    }

    handleKeyDown = event => {
        if (event.key === 'Enter') {
            event.preventDefault();
        }
    }

    render() {
        let {name, task, solution, author} = this.state;
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Название:
                        <input type="text" value={name} onChange={this.handleChangeName}
                               onKeyDown={this.handleKeyDown}/>
                        Условие:
                        <input type="text" value={task} onChange={this.handleChangeTask}
                               onKeyDown={this.handleKeyDown}/>
                        Решение:
                        <input type="text" value={solution} onChange={this.handleChangeSolution}
                               onKeyDown={this.handleKeyDown}/>
                        Автор:
                        <input type="text" value={author} onChange={this.handleChangeAuthor}
                               onKeyDown={this.handleKeyDown}/>
                    </label>
                    <input type="submit" value="Сохранить" className="btn btn-primary"/>
                </form>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        problems_list: state.userReducer.problems
    }
}

const mapDispatchToProps = (dispatch) => {
    return {}
};

export default connect(mapStateToProps, mapDispatchToProps)(EditForm);
