import userService from "../User";

export function fetchIsLogged(payload) {
    return {
        type: 'USER_ISLOGGED',
        payload
    }
}

export function login(login, password) {
    return dispatch => {
        return userService.login(login, password).then((data) => {
            dispatch(fetchIsLogged(data));
        })
    }
}
