import User from '../User/user'
import {problems} from '../globals'

let initialState ={
//     user: new User('', '', false),
    error: '',
    problems: problems
}

const userReducer = (state = initialState, action) => {
    switch(action.type) {
        case 'USER_ISLOGGED':
            console.log('login')
            console.log(action.user.user, 'action')
            return {
                ...state,
                user: action.user.user,
                error: ''
            };
        case 'USER_ISUNLOGGED':
            console.log('logout')
            return initialState
        case 'FAILED-TOLOGIN':
            return {
                ...state,
                error: action.error
            }
        case 'EDIT-PROBLEM':
            return {
                ...state,
                problems: problems
            }
        default:
            return initialState;
    }
}

export default userReducer;
